				$p['role_id'] = $data['role_id'];
				$p['resource_id'] = $data['resource_id'];
				$p['permission'] = $data['permission'];
				$p->save();
				
				return is_numeric($id) ? $id : $p->getIncremented ();
			}
			return null;
		}
	
