			$p = new Doctrine_Collection('AdminPermissions');
			$defaultRes = AdminResources::getDefaultModuleList();
			$i = 0;
			
			foreach ($defaultRes as $id => $resource){
				$p[$i]['role_id'] = $roleID;
				$p[$i]['resource_id'] = $id;
