		 								->from ( 'AdminRoles r' )
        								->leftJoin ( 'r.AdminUser u' )
        								->where ( 'u.user_id = ?', $user_id)
        								->andWhere ( 'r.role_id = ?', 1)
        								->limit(1)
        								->execute(array(), Doctrine::HYDRATE_ARRAY);
		  
		 return $record[0]['total'];
	}
	
