	 *
	 * @return ArrayObject
	 */
	public static function getList() {
		$items = array ();
		$arrTypes = Doctrine::getTable ( 'AdminUser' )->findAll ();
		foreach ( $arrTypes->getData () as $c ) {
		    if( $c['isp_id'] != Isp::getCurrentId() ) {
		        continue;
		    }
			$items [$c ['user_id']] = $c ['lastname'] . " " . $c['firstname'];
		}
		return $items;
	}
	
