											->leftJoin ( 'p.AdminResources s' )
											->where ( 'MD5(i.email) = ?', $md5email)
											->execute(array(), Doctrine::HYDRATE_ARRAY);
				
		if($record){
			return !empty($record[0]) ? $record[0] : FALSE;
		}else{
			return NULL;
		}
	}
	
	
