	/**
	 * getAllInfo
	 * Get a record by ID
	 * @param $id
	 * @return ARRAY Record
	 */
	public static function getAllInfo($id, $fields = "*", $where = "") {
		try {
			$dq = Doctrine_Query::create ()
								->from ( 'Customers c' )
								->leftJoin ( 'c.CompanyTypes cts' )
								->leftJoin ( 'c.Legalforms l' )
								->leftJoin ( 'c.Addresses a' )
								->leftJoin ( 'a.Countries ct' )
								->leftJoin ( 'c.Contacts cn' )
                                ->leftJoin ( 'cn.ContactsTypes cnts' )
								->leftJoin ( 'c.Statuses s' )
								->leftJoin ( 'c.CustomAttributesValues cav ON cav.external_id = c.customer_id' )
								->leftJoin ( 'cav.CustomAttributes ca' )
								->where ( 'c.customer_id = ?', $id )
                                ->andWhere( "c.isp_id = ?", Isp::getCurrentId() );
			
			if(!empty($fields) && $fields != "*"){
				$dq->select ( $fields );
			}
			
			if (! empty ( $where )) {
				$dq->andWhere ( $where );
			}
			
			$customer = $dq->execute ( array (), Doctrine_Core::HYDRATE_ARRAY );
			
			if (isset ( $customer [0] )) {
				// workaround for missing isp_id. Older customers, created before multi-isp suppor does't have an isp_id set
				$customer[0]['isp_id'] = (!isset($customer[0]['isp_id']) || intval($customer[0]['isp_id']) < 1) ? 1 : intval($customer[0]['isp_id']);
				
				// Add "fullname" value, needed almost everywhere
				if ( !empty($customer[0]['company']) ) {
					$customer[0]['fullname'] = $customer[0]['company'];
				} else {
					$customer[0]['fullname'] = !empty($customer[0]['lastname']).' '.!empty($customer [0]['firstname']);	
				}
				
				$customer[0]['full_personname'] = !empty($customer[0]['lastname']) ? $customer[0]['lastname'] : null;
				$customer[0]['full_personname'] .= !empty($customer[0]['firstname']) ? $customer[0]['firstname'] : null;
				
				return $customer [0];
			} else {
				return array ();
			}
		
		} catch ( Exception $e ) {
			die ( $e->getMessage () );
		}
	
	}
	
