	/**
	 * Get a list ready for the html select object
	 * @return array
	 */
	public static function getList($empty = false, array $criterias = array()) {
		$items = array ();
		$registry = Shineisp_Registry::getInstance ();
		$translations = $registry->Zend_Translate;
		
		if ($empty === true) {
			$items [] = $translations->translate ( 'Select ...' );
		}elseif(is_string($empty)){
			$items [] = $translations->translate ( $empty );
		}
		
		$Customers = Doctrine_Query::create ()->from ( 'Customers u' );
        
        $auth = Zend_Auth::getInstance ();
        if( $auth->hasIdentity () ) {
            $logged_user= $auth->getIdentity ();
            $Customers->andWhere( "u.isp_id = ?", $logged_user['isp_id']);
        }else{
        	$Customers->andWhere( "u.isp_id = ?", Isp::getCurrentId() );
        }          
		
		if (count ( $criterias ) > 0) {
			foreach ( $criterias as $criteria ) {
				$Customers->addWhere ( $criteria ['where'], $criteria ['params'] );
			}
		}
		
		$Customers->orderBy ( 'lastname' );
		$arrCustomers = $Customers->execute ( null, Doctrine::HYDRATE_ARRAY );
				
		foreach ( $arrCustomers as $c ) {
			$items [$c ['customer_id']] = Shineisp_Commons_Utilities::Capitalize($c ['lastname'] . " " . $c ['firstname'], true);
			if (! empty ( $c ['company'] )) {
				$items [$c ['customer_id']] .= " - " . ucwords($c ['company']);
			}
		}
		return $items;
	}
	
	/**
	 * Get a list of email of all the customers
	 * @return array
	 */
	public static function getEmailList($empty = false) {
		$items = array ();
		$registry = Shineisp_Registry::getInstance ();
		$translations = $registry->Zend_Translate;
		
		if ($empty) {
			$items [] = $translations->translate ( 'Select ...' );
		}

		$customer = Doctrine_Query::create ()->from ( 'Customers c' )->andWhere( "c.isp_id = ?", Isp::getCurrentId() )->orderBy ( 'lastname, firstname, company' )->execute (array (), Doctrine_Core::HYDRATE_ARRAY);
		
		foreach ( $customer as $c ) {
		    if(!empty($c ['emaillastname'])){
                $items [$c ['customer_id']] = Shineisp_Commons_Utilities::Capitalize($c ['emaillastname'] . " (" . $c ['email'] . ")", true);
            }else{
                $items [$c ['customer_id']] = Shineisp_Commons_Utilities::Capitalize($c ['email'], true);
            }
			if (! empty ( $c ['company'] )) {
				$items [$c ['customer_id']] .= " - " . ucwords($c ['company']);
			}
		}
		
		return $items;
	}
	
	/**
	 * login
	 * Check if the customer exist
	 * @param $email
	 * @param $password
	 * @return boolean
	 */
	public static function login($email, $password) {
		try {
			$isMD5 = false;
			
			$dbUser = Doctrine_Query::create ()
								->from ( 'Customers u' )
								->where ( 'email = ? AND status_id = ?', array ($email, Statuses::id('active', 'customers') ) )
                                ->andWhere( "u.isp_id = ?", Isp::getCurrentId() )
								->limit(1)
								->fetchArray();
								
			if ( $dbUser && isset($dbUser[0]) ) {
				$dbUser = $dbUser[0];
				$dbPass = $dbUser['password'];
				
				if ( strlen($dbPass) > 32 && substr($dbPass,0,1) == '$') {
					$userPassword = crypt($password, $dbPass);
				} else {
					$isMD5 = true;
					$userPassword = md5($password);
				}
				
				if ( $userPassword == $dbPass ) {
					//* if password was crypted in plain MD5, force convertion to crypt
					if ( $isMD5 ) {
						$cryptPassword = crypt($password);
						
						Doctrine_Query::create ()->update ( 'Customers u' )
												 ->set ( 'u.password', '?', $cryptPassword )
												 ->where ( 'email = ?', $email )
												 ->limit(1)
												 ->execute ();					
					} 
	
					// Auth OK
					return $dbUser;				
				}				
			}
			
			return array();
		 
 		} catch ( Exception $e ) {
			echo $e->getMessage ();
			die ();
		}
	}
	
	/**
	 * Get the list of best customers
	 * @return ArrayObject
	 */
	public static function Hitparade() {
		$currency = Shineisp_Registry::getInstance ()->Zend_Currency;
		$translator = Shineisp_Registry::getInstance ()->Zend_Translate;
		$dq   = Doctrine_Query::create ()->select ( "i.invoice_id, c.customer_id as id, c.lastname as lastname, c.firstname as firstname, c.company as company, SUM(o.grandtotal) as grandtotal" )
					->from ( 'Invoices i' )
					->leftJoin ( 'i.Customers c' )
					->leftJoin ( 'i.Orders o' )
					->groupBy( 'o.customer_id' )
					->orderBy ( 'c.lastname' )
					->orderBy ( 'SUM(o.grandtotal) desc' )
					->limit(20);
                    
        $auth = Zend_Auth::getInstance ();
        if( $auth->hasIdentity () ) {
            $logged_user= $auth->getIdentity ();
            $dq->whereIn( "c.isp_id", $logged_user['isp_id']);
        }else{
        	$dq->andWhere( "c.isp_id = ?", Isp::getCurrentId() );
        } 
        
	    $records['data']   = $dq->execute ( null, Doctrine::HYDRATE_ARRAY );
        
		for($i=0;$i<count($records['data']);$i++){
			$records['data'][$i]['grandtotal']    = $currency->toCurrency($records['data'][$i]['grandtotal'], array('currency' => Settings::findbyParam('currency')));
		}
		
		// adding the index reference
		$records['index'] = "id";
		
		// Create the header table columns
		$records['fields'] = array( 'lastname' => array('label' => $translator->translate('Last name'), 'attributes' => array('class' => 'visible-lg visible-md hidden-xs')),
									'firstname' => array('label' => $translator->translate('First name'), 'attributes' => array('class' => 'visible-lg visible-md hidden-xs')),
									'company' => array('label' => $translator->translate('Company')),
									'grandtotal' => array('label' => $translator->translate('Total')));
		
		return $records;
	}
	
	/**
	 * Summary of all the customers stautus
	 * @return array
	 */
	public static function summary() {
		$chart = "";
		$translator = Shineisp_Registry::getInstance ()->Zend_Translate;
		
		// Get the customer summary values
		$dq   = Doctrine_Query::create ()
									->select ( "customer_id, count(*) as items, s.status as status" )
									->from ( 'Customers c' )
									->leftJoin ( 'c.Statuses s' )
									->where("s.section = 'customers'")
                                    ->addWhere( "c.isp_id = ?", Isp::getCurrentId() )
									->groupBy('s.status');

        $auth = Zend_Auth::getInstance ();
        if( $auth->hasIdentity () ) {
            $logged_user= $auth->getIdentity ();
            $dq->andWhere( "c.isp_id = ?", $logged_user['isp_id']);
        } 
        
        $datarecords = $dq->execute(array (), Doctrine_Core::HYDRATE_ARRAY);

		$records['data'] = $datarecords;
		$records['fields'] = array('items' => array('label' => $translator->translate('Items')), 'status' => array('label' => $translator->translate('Status')));
		
		return $records;
	}
	
	
