	/**
	 * setCustomerPassword
	 * set the password of the customer 
	 * @param $email
	 * @param $newPwd
	 * @return Boolean
	 */
	public static function setCustomerPassword($email, $newPwd) {
		$q = Doctrine_Query::create ()->update ( 'Customers' )
				->set ( 'password', '?', crypt ( $newPwd ) )
				->where ( 'MD5(email) = ?', $email );
		return $q->execute ();
	}
	
