			}
		}
	
		// Create the XLS
		$objWriter = new PHPExcel_Writer_Excel2007($objPHPExcel);
		$objWriter->save(PUBLIC_PATH . "/tmp/" . __CLASS__ . '.xlsx');
	
		die(json_encode(array('url' => "/tmp/" . __CLASS__ . ".xlsx")));
	}
}