										->from ( 'OrdersItems oi' )
										->where ( "detail_id = ?", $id )
										->limit ( 1 );
		
		$retarray = $retarray ? Doctrine_Core::HYDRATE_ARRAY : null;
		$item = $dq->execute ( array (), $retarray );
		return $item;
	}
	
