class WhoisServers extends BaseWhoisServers
{
 
	/**
     * getList
     * Get a list ready for the html select object
     * @return array
     */
    public static function getList($empty=false) {
        $items = array ();
        
        $data = Doctrine_Query::create ()->from ( 'WhoisServers' )->orderBy('tld')->execute ( array (), Doctrine_Core::HYDRATE_ARRAY );
        
        if($empty){
            $items[] = "";
        }
        
        foreach ( $data as $c ) {
            $items [$c ['server_id']] = $c ['tld'];
        }
        
        return $items;
    }
    
	/**
     * Get all the whois records
     * 
     * 
     * @return Array
     */
    public static function getAll() {
        return Doctrine_Query::create ()->from ( 'WhoisServers' )->execute ( array (), Doctrine_Core::HYDRATE_ARRAY );
    }
}